mapper.update:
UPDATE
    cart
SET num=#{num},
    modified_user=#{modifiedUser},
    modified_time=#{modifiedTime}
WHERE cid = #{cid}

mapper.select:
SELECT *
FROM cart
WHERE uid = #{uid}
  AND pid = #{pid}

SELECT cid, uid, pid, cart.price, cart.num, product.title, product.price AS realPrice, product.image FROM cart LEFT JOIN product ON cart.pid = product.id WHERE cid IN (
#{cid}
) ORDER BY cart.created_time DESC
